import { FunctionComponent, useState } from "react";

interface HeaderPageDesertsProps {}

interface DessertCard {
  title: string;
  description: string;
  imageUrl: string;
}

const initialDesserts: DessertCard[] = [
  {
    title: "Tiramisu",
    description:
      "Italian classic with mascarpone, espresso-soaked ladyfingers, and cocoa.",
    imageUrl:
      "https://images.unsplash.com/photo-1571877227200-a0d98ea607e9?auto=format&fit=crop&w=1200&q=80",
  },
  {
    title: "New York Cheesecake",
    description: "Creamy baked cheesecake with a buttery graham cracker crust.",
    imageUrl:
      "https://images.unsplash.com/photo-1533134242443-d4fd215305ad?auto=format&fit=crop&w=1200&q=80",
  },
  {
    title: "Malabi",
    description:
      "Silky Middle Eastern milk pudding topped with rose syrup and pistachios.",
    imageUrl:
      "https://images.unsplash.com/photo-1488477181946-6428a0291777?auto=format&fit=crop&w=1200&q=80",
  },
];

const HeaderPageDeserts: FunctionComponent<HeaderPageDesertsProps> = () => {
  const [desserts, setDesserts] = useState<DessertCard[]>(initialDesserts);
  const [newDessert, setNewDessert] = useState<DessertCard>({
    title: "",
    description: "",
    imageUrl: "",
  });
  const [editingIndex, setEditingIndex] = useState<number | null>(null);
  const [editingDessert, setEditingDessert] = useState<DessertCard>({
    title: "",
    description: "",
    imageUrl: "",
  });

  const addDessert = () => {
    if (
      !newDessert.title.trim() ||
      !newDessert.description.trim() ||
      !newDessert.imageUrl.trim()
    ) {
      return;
    }

    setDesserts((prev) => [...prev, { ...newDessert }]);
    setNewDessert({ title: "", description: "", imageUrl: "" });
  };

  const startEdit = (index: number) => {
    setEditingIndex(index);
    setEditingDessert({ ...desserts[index] });
  };

  const saveEdit = () => {
    if (
      editingIndex === null ||
      !editingDessert.title.trim() ||
      !editingDessert.description.trim() ||
      !editingDessert.imageUrl.trim()
    ) {
      return;
    }

    setDesserts((prev) =>
      prev.map((item, index) =>
        index === editingIndex ? { ...editingDessert } : item,
      ),
    );
    setEditingIndex(null);
  };

  const cancelEdit = () => {
    setEditingIndex(null);
    setEditingDessert({ title: "", description: "", imageUrl: "" });
  };

  return (
    <section className="container py-4">
      <h4 className="display-6 text-center mb-4" style={{ color: "#5F9598" }}>
        Sweet Desserts üç∞
      </h4>

      <div
        className="p-3 rounded-3 mb-4"
        style={{ backgroundColor: "#061E29", border: "1px solid #1D546D" }}
      >
        <h6 className="mb-3" style={{ color: "#F3F4F4" }}>
          Add Dessert Card
        </h6>
        <div className="row g-2">
          <div className="col-md-3">
            <input
              className="form-control"
              placeholder="Dessert title"
              value={newDessert.title}
              onChange={(e) =>
                setNewDessert((prev) => ({ ...prev, title: e.target.value }))
              }
            />
          </div>
          <div className="col-md-5">
            <input
              className="form-control"
              placeholder="Dessert description"
              value={newDessert.description}
              onChange={(e) =>
                setNewDessert((prev) => ({
                  ...prev,
                  description: e.target.value,
                }))
              }
            />
          </div>
          <div className="col-md-3">
            <input
              className="form-control"
              placeholder="Image URL"
              value={newDessert.imageUrl}
              onChange={(e) =>
                setNewDessert((prev) => ({ ...prev, imageUrl: e.target.value }))
              }
            />
          </div>
          <div className="col-md-1 d-grid">
            <button
              className="btn"
              style={{ backgroundColor: "#1D546D", color: "#F3F4F4" }}
              onClick={addDessert}
            >
              Add
            </button>
          </div>
        </div>
      </div>

      <div className="row">
        {desserts.map((dessert, index) => (
          <div className="col-md-4 mb-4" key={`${dessert.title}-${index}`}>
            <div
              className="card h-100 shadow-sm"
              style={{
                backgroundColor: "#061E29",
                border: "1px solid #1D546D",
              }}
            >
              {editingIndex === index ? (
                <div className="p-3">
                  <input
                    className="form-control mb-2"
                    value={editingDessert.title}
                    onChange={(e) =>
                      setEditingDessert((prev) => ({
                        ...prev,
                        title: e.target.value,
                      }))
                    }
                    placeholder="Dessert title"
                  />
                  <input
                    className="form-control mb-2"
                    value={editingDessert.description}
                    onChange={(e) =>
                      setEditingDessert((prev) => ({
                        ...prev,
                        description: e.target.value,
                      }))
                    }
                    placeholder="Dessert description"
                  />
                  <input
                    className="form-control mb-3"
                    value={editingDessert.imageUrl}
                    onChange={(e) =>
                      setEditingDessert((prev) => ({
                        ...prev,
                        imageUrl: e.target.value,
                      }))
                    }
                    placeholder="Image URL"
                  />
                  <div className="d-flex gap-2">
                    <button
                      className="btn btn-sm"
                      style={{ backgroundColor: "#1D546D", color: "#F3F4F4" }}
                      onClick={saveEdit}
                    >
                      Save
                    </button>
                    <button
                      className="btn btn-sm btn-secondary"
                      onClick={cancelEdit}
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              ) : (
                <>
                  <img
                    src={dessert.imageUrl}
                    className="card-img-top"
                    alt={dessert.title}
                    style={{ height: "220px", objectFit: "cover" }}
                  />
                  <div className="card-body">
                    <h5 className="card-title" style={{ color: "#F3F4F4" }}>
                      {dessert.title}
                    </h5>
                    <p className="card-text" style={{ color: "#5F9598" }}>
                      {dessert.description}
                    </p>
                  </div>
                  <div className="card-footer bg-transparent border-0 pt-0 pb-3 px-3">
                    <button
                      className="btn btn-sm w-100"
                      style={{
                        backgroundColor: "#1D546D",
                        color: "#F3F4F4",
                        border: "1px solid #5F9598",
                      }}
                      onClick={() => startEdit(index)}
                    >
                      Edit Dessert
                    </button>
                  </div>
                </>
              )}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default HeaderPageDeserts;
